import React, { useState } from 'react';
import './App.css';

function App() {
  const [name, setName] = useState('');
  const [school, setSchool] = useState('');
  const [total, setTotal] = useState('');
  const [max, setMax] = useState('');
  const [show, setShow] = useState(false);

  const handleShow = () => {
    if (name && school && total && max) {
      setShow(true);
    } else {
      alert("Please fill all fields!");
    }
  };

  const percentage = ((total / max) * 100).toFixed(2);

  return (
    <div className="container">
      <h1 className="title">Student Details:</h1>

      {!show ? (
        <div className="form">
          <input type="text" placeholder="Enter Name" value={name} onChange={(e) => setName(e.target.value)} />
          <input type="text" placeholder="Enter School" value={school} onChange={(e) => setSchool(e.target.value)} />
          <input type="number" placeholder="Enter Total Marks" value={total} onChange={(e) => setTotal(e.target.value)} />
          <input type="number" placeholder="Enter Maximum Marks" value={max} onChange={(e) => setMax(e.target.value)} />
          <button onClick={handleShow}>Show Score</button>
        </div>
      ) : (
        <div className="result">
          <p><strong>Name:</strong> <span className="blue">{name}</span></p>
          <p><strong>School:</strong> <span className="red">{school}</span></p>
          <p><strong>Total:</strong> <span className="purple">{total} Marks</span></p>
          <p><strong>Score:</strong> <span className="green">{percentage}%</span></p>
        </div>
      )}
    </div>
  );
}

export default App;
