import React, { useState } from 'react';
import '../Stylesheets/mystyle.css';

function CalculateScore() {
  const [name, setName] = useState('');
  const [school, setSchool] = useState('');
  const [total, setTotal] = useState('');
  const [goal, setGoal] = useState('');
  const [score, setScore] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    const percentage = ((parseFloat(total) / parseFloat(goal)) * 100).toFixed(2);
    setScore(percentage);
  };

  return (
    <div className="score-container">
      <h2 className="title">Student Details:</h2>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Enter Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Enter School"
          value={school}
          onChange={(e) => setSchool(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Enter Total Marks"
          value={total}
          onChange={(e) => setTotal(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Enter Maximum Marks"
          value={goal}
          onChange={(e) => setGoal(e.target.value)}
          required
        />
        <button type="submit">Show Score</button>
      </form>

      {score && (
        <div className="output">
          <p><strong className="label">Name:</strong> <span className="blue">{name}</span></p>
          <p><strong className="label">School:</strong> <span className="red">{school}</span></p>
          <p><strong className="label">Total:</strong> {total}Marks</p>
          <p><strong className="label">Score:</strong><span className="green">{score}%</span></p>
        </div>
      )}
    </div>
  );
}

export default CalculateScore;
